import java.util.ArrayList;
import java.util.List;

class Solution {
    public List<Integer> diffWaysToCompute(String expression) {
        List<Integer> results = new ArrayList<>();
        for (int i = 0; i < expression.length(); i++) {
            char ch = expression.charAt(i);
            if (ch == '+' || ch == '-' || ch == '*') {
                List<Integer> leftResults = diffWaysToCompute(expression.substring(0, i));
                List<Integer> rightResults = diffWaysToCompute(expression.substring(i + 1));
                for (int left : leftResults) {
                    for (int right : rightResults) {
                        switch (ch) {
                            case '+':
                                results.add(left + right);
                                break;
                            case '-':
                                results.add(left - right);
                                break;
                            case '*':
                                results.add(left * right);
                                break;
                        }
                    }
                }
            }
        }
        if (results.isEmpty()) {
            results.add(Integer.parseInt(expression));
        }
        return results;
    }

    private void helper(String expression, StringBuilder sb, int i, List<Integer> ans) {
        if (i == expression.length()) {
            ans.add(calculate(sb.toString()));
            return;
        }

        if (Character.isDigit(expression.charAt(i))) {
            int numStart = i;
            while (i < expression.length() && Character.isDigit(expression.charAt(i))) {
                i++;
            }
            sb.append(expression.substring(numStart, i));
            helper(expression, sb, i, ans);
            sb.delete(sb.length() - (i - numStart), sb.length());
        } else {
            sb.append(expression.charAt(i));
            helper(expression, sb, i + 1, ans);
            sb.deleteCharAt(sb.length() - 1);
            if (i < expression.length() - 1 && (expression.charAt(i + 1) == '+' || expression.charAt(i + 1) == '-'
                    || expression.charAt(i + 1) == '*')) {
                sb.append('(');
                helper(expression, sb, i + 1, ans);
                sb.append(')');
            }
        }
    }

    private int calculate(String expression) {
        // Remove all spaces from the expression
        expression = expression.replaceAll("\\s+", "");

        return evaluateExpression(expression, 0)[0];
    }

    private int[] evaluateExpression(String expression, int start) {
        int num = 0;
        char operator = '+';
        List<Integer> results = new ArrayList<>();
        int i = start;

        while (i < expression.length()) {
            char ch = expression.charAt(i);
            if (Character.isDigit(ch)) {
                num = num * 10 + (ch - '0');
            } else if (ch == '(') {
                int[] subResult = evaluateExpression(expression, i + 1);
                num = subResult[0];
                i = subResult[1];
            } else if (ch == ')') {
                break; // End of current subexpression
            } else {
                calculateOperation(operator, num, results);
                operator = ch;
                num = 0;
            }
            i++;
        }

        calculateOperation(operator, num, results);

        // Calculate the final result based on all results in the list
        int total = 0;
        for (int result : results) {
            total += result;
        }

        return new int[] { total, i };
    }

    private void calculateOperation(char operator, int num, List<Integer> results) {
        switch (operator) {
            case '+':
                results.add(num);
                break;
            case '-':
                results.add(-num);
                break;
            case '*':
                results.set(results.size() - 1, results.get(results.size() - 1) * num);
                break;
        }
    }
}

