            s.pop();
        }

        int endingIndex = result.size()-1;
        for(int i = result.size()-1; i >= 0; i--){
            if(result[i] != '0'){
                endingIndex = i;
            }
        }

        result = result.substr(0,endingIndex+1);
        reverse(result.begin(),result.end());
        return result;
    }
