```class Solution {
public:
    string getSmallestString(string s, int k) {
        // Time = O(n), Space = O(1)
        int i = 0;
        while (i < s.size() && k > 0) {
            int diff = 'z' - s[i] + 1;
            if (s[i] != 'a' && diff <= k) {
                s[i] = 'a';
                k -= diff;
            } else if (s[i] != 'a') {
                int diff = min(k, s[i] - 'a');
                s[i] -= diff;
                k -= diff;
            }
            i++;
        }
        return s;
    }
};

