class Solution {
private: 
    unordered_map<int, int> memo;
public:
    int fib(int n) {
        if (memo.find(n) != memo.end()) {
            return memo[n];
        }
        if (n == 0) return 0;
        if (n == 1 || n == 2) return 1;
        int result = fib(n - 1) + fib(n - 2);
        memo[n] = result;
        return result;
    }
};

