    //Train preparation
    public List<Train> readyTrain(){
        List<Train> trains= new ArrayList<>();

        Train mumTolkn= new Train();
        mumTolkn.setTrainName("Mumbai to lucknow");
        mumTolkn.setTrainNo(2);
        mumTolkn.setCoatchs(readyCoatchs());
        trains.add(mumTolkn);
        Train mumToBan= new Train();
        mumToBan.setTrainName("Mumbai to Bangolore");
        mumToBan.setTrainNo(1);
        mumToBan.setCoatchs(readyCoatchs());
        trains.add(mumToBan);
        trainClass.setTrains(trains);

        return trains;

    }

    //Coatch Preparation
    private List<Coatch> readyCoatchs(){
        List<Coatch> coatchs= new ArrayList<>();
        Coatch coatch1 = new Coatch();
        coatch1.setCoatchNo("M1");
        coatch1.setStatus(0);
        coatch1.setSeats(readySeats());
        coatchs.add(coatch1);

        Coatch coatch2 = new Coatch();
        coatch2.setCoatchNo("M2");
        coatch2.setStatus(0);
        coatch2.setSeats(readySeats());
        coatchs.add(coatch2);

        Coatch coatch3 = new Coatch();
        coatch3.setCoatchNo("M3");
        coatch3.setStatus(0);
        coatch3.setSeats(readySeats());

        coatchs.add(coatch3);

        return coatchs;
    }



    //Seat Preparation
    private List<Seat> readySeats(){
        List<Seat> seats= new ArrayList<>();
        int totalSeats=32;
        int seatInOneBlock=8;
        int totalBlock= totalSeats/seatInOneBlock;
        int seatNumberStating=1;
        final String lowerBirth="LB";
        final String middleBirth="MB";
        final String upperBirth="UB";
        final String sideLowerBirth="SLB";
        final String sideUpperBirth="SUB";

        for(int i=1;i<=totalBlock;i++){
            for(int j=1;j<=seatInOneBlock;j++){
                Seat seat= new Seat();
                    if (j==1|| j==4) {
                        seat.setSeatPosition(lowerBirth);
                    }
                    if (j==2|| j==5) {
                        seat.setSeatPosition(middleBirth);
                    }
                    if (j==3|| j==6) {
                        seat.setSeatPosition(upperBirth);
                    }
                    if (j==7) {
                        seat.setSeatPosition(sideLowerBirth);
                    }
                    if (j==8) {
                        seat.setSeatPosition(sideUpperBirth);
                    }
                    seat.setStatus(0);
                seat.setSeatNo(seatNumberStating);
                seats.add(seat);
                seatNumberStating++;
            }
        }

       return seats;
    }

}

